import { tezos } from "./tezos";

export const claimOwner = async() => {
  try{
    const contractInstance = await tezos.wallet.at("KT1HeQbzgEqKgrdbU7tBPfQoAJffaF9ZCyKX");
    const op = await contractInstance.methods.claimOwner().send();
    await op.confirmation(1);
  } catch (err) {
    throw err;
  }
}

export const claimCP = async() => {
  try{
    const contractInstance = await tezos.wallet.at("KT1HeQbzgEqKgrdbU7tBPfQoAJffaF9ZCyKX");
    const op = await contractInstance.methods.claimCounterparty({secret: "0xc2e588e23a6c8b8192da64af45b7b603ac420aefd57cc1570682350154e9c04e"}).send();
    await op.confirmation(1);
  } catch (err) {
    throw err;
  }
}

export const setBalanceOwnerEscrow = async (fromOwner) => {
  try{
    // Create Escrow instance
    const contractInstance = await tezos.wallet.at("KT1HeQbzgEqKgrdbU7tBPfQoAJffaF9ZCyKX");
    // Add balance of cp and owner
    const op = await contractInstance.methods.addBalanceOwner().send({
      amount: fromOwner,
      mutez: true
    });
    await op.confirmation(1);
    alert("Transaction successful.");
  } catch (err) {
    throw err;
  }
};

export const setBalanceCPEscrow = async (fromCP) => {
  try{
    // Create Escrow instance
    const contractInstance = await tezos.wallet.at("KT1HeQbzgEqKgrdbU7tBPfQoAJffaF9ZCyKX");
    // Add balance of cp and owner
    const op = await contractInstance.methods.addBalanceCounterparty().send({
      amount: fromCP,
      mutez: true,
    });
    await op.confirmation(1);

  } catch (err) {
    throw err;
  }
};
