import { useState, useEffect } from "react";
import { connectWallet, getAccount } from "./utils/wallet";
import { fetchStorage } from "./utils/tzkt";
import { initializeEscrow } from "./utils/operation";

const App = () => {
  const [loading, setLoading] = useState(false);
  const [account, setAccount] = useState("");
  const [account_cp, setAccount_cp] = useState("");

  useEffect(() => {
    (async () => {
      const account = await getAccount();
      setAccount(account);
      const account_cp = await getAccount();
      setAccount_cp(account_cp);
    })();
  }, []);

  const onConnectWallet = async () => {
    await connectWallet();
    const account = await getAccount();
    setAccount(account);
  };

  const onConnectWalletCP = async () => {
    await connectWallet();
    const account_cp = await getAccount();
    setAccount_cp(account_cp);
  };

  const submitEscrow = async (owner, fromOwner, cp, fromCP, epoch, hash) => {
    try{
      const storage = await fetchStorage();
      initializeEscrow();
    }
    catch(err){
      throw err;
    }
    
  }
  
  return (
    <div className="h-100">
      <div className="d-flex flex-row align-items-center" style={{padding:40, justifyContent: 'space-between'}}>
        <div></div>
        <div className="d-flex flex-column justify-content-center align-items-center h-100">
        <div className="py-1">Deposit Funds</div>
        </div>
        <div className="d-flex flex-column justify-content-center align-items-center h-100">
        <div className="py-1">Deposit Funds</div>
        <div className="py-1">Owner</div>
        <form>
          <label>
            Amount:
            <textarea name="owner_amount"/>
          </label>
          <button onClick={submitEscrow} className="btn btn-outline-info">
            Submit
        </button>
        </form>
        <div className="py-1">Counter Party</div>
        <form>
          <label>
            Amount:
            <textarea name = "cp_amount"/>
          </label>
          <button onClick={submitEscrow} className="btn btn-outline-info">
            Submit
        </button>
        </form>
        
      </div>
      </div>
      
    </div>
  );
};

export default App;
